class TrackDescriptor:
    def __init__(self, starts_diagonal, slope_start, bank_start, track_curve, slope_end, bank_end):
        self.starts_diagonal = starts_diagonal
        self.slope_start = slope_start
        self.bank_start = bank_start
        self.track_curve = track_curve
        self.slope_end = slope_end
        self.bank_end = bank_end

TRACK_DESCRIPTORS = {k: TrackDescriptor(*desc) for k, desc in TRACK_DESCRIPTORS.items()}